import { createStore } from 'vuex'

export default createStore({
  state: {
    userInfo:null,
    isLogin: false,
    isLoginError: false
  },
  getters: {
  },
  mutations: {
    loginSuccess(state, payload) {
      state.isLogin = true;
      state.isLoginError = false;
      state.userInfo = payload;
    },
    loginError(state) {
      state.isLogin = false;
      state.isLoginError = false;
      state.userInfo = null;
    },
    logout(state) {
      state.isLogin = false;
      state.isLoginError = false;
      state.userInfo = null;
    },
  },
  actions: {
  },
  modules: {
  }
})
