import { createApp } from 'vue'
import App from './App.vue'
import router from './router'
import store from './store'
import { loadFonts } from './plugins/webfontloader'

loadFonts()

//import 'bootstrap/dist/css/bootstrap.min.css'
//import "bootstrap/dist/js/bootstrap.min.js"
//import 'bootstrap'

// font-awesome과 관련된 import를 정의
import { library } from '@fortawesome/fontawesome-svg-core'
import { faPenToSquare, faCheck, faTrash, faPlus, faChartPie, faCalendar } from '@fortawesome/free-solid-svg-icons'
//import { faChartPieSimple } from '@fortawesome/free-regular-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/vue-fontawesome'
library.add(faPenToSquare, faCheck, faTrash, faPlus, faChartPie, faCalendar)
// 위에 createApp을 통해 생성한 Vue Application 인스턴스의 component API 활용
App.component('font-awesome-icon', FontAwesomeIcon)

createApp(App).use(store).use(router).mount('#app')

